2.query:
fetch logs, from:now()-24d
| fieldsKeep timestamp,transaction_type
| summarize transactions =  count(),by:{transaction_type,bin(timestamp,1h)}

3.query:
fetch logs, from:now()-24d
//| filter (event.provider == "withdrawal_payments_15mins" or event.provider == "withdrawal_payments_60mins" or event.provider == "deposit_payments_5mins")
| filter contains(transaction_status, "PENDING")
| summarize no_of_transactions=count(),by:{bin(timestamp, 5m)}

4.query:
fetch logs, from: now()-30d
//| filter (event.provider == "withdrawal_payments_15mins" or event.provider == "withdrawal_payments_60mins" or event.provider == "deposit_payments_5mins")
| fieldsAdd hour=formatTimestamp(timestamp,format:"hh"), day_of_week=formatTimestamp(timestamp,format:"EE")
| filterOut (day_of_week  == "Sat" or day_of_week == "Sun") or (toLong(hour) <= 08 or toLong(hour) >= 17)
| summarize Transaction_amount = count(), by:{day_of_week}

5.query:
fetch logs, from:now()-1h, scanLimitGBytes:-1
//| filter (event.provider == "withdrawal_payments_15mins" or event.provider == "withdrawal_payments_60mins" or event.provider == "deposit_payments_5mins")
| summarize count(), by:{transaction_status}

